                if($key === 'tag') {
                    $this->tag = Tag::create([
                        'feeling'   => $data[0],
                        'language'  => $data[1],
                        'occasion'  => $data[2],
                    ]);
                }

                /** Create Movies && set up relation */
                if($key === 'movies') {
                    if(count($data) > 1) {
                        foreach($data as $movie)  {
                            $movie = Movie::create($movie);
                            $this->tag->movies()->attach($movie->id);
                        }
                    } else {
                        $movie = Movie::create($data);
                        $this->tag->movies()->attach($movie->id);
                    }
